"use strict";

var mongoose = require("mongoose");
var auteurSchema = mongoose.Schema({
  nomauteur: {
    type: String,
    required: true
  },
  email: {
    type: String,
    required: true,
    unique: true
  },
  numtel: {
    type: Number,
    required: true,
    unique: true
  }
});
module.exports = mongoose.model('auteur', auteurSchema);
